import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import BookItem from './BookItem';
import './EffectMemo.css';

const EffectMemo = () => {
  const [books, setBooks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  // fetch book data from API on mount
  useEffect(() => {
    axios.get('https://my-api.com/books')
      .then(res => setBooks(res.data))
      .catch(err => console.log(err));
  }, []);

  // memoize filtered book list
  const filteredBooks = useMemo(() => {
    return books.filter(book => book.title.toLowerCase().includes(searchTerm.toLowerCase()));
  }, [books, searchTerm]);

  return (
    <div className="book-list">
      <h1>My Book List</h1>
      <input type="text" placeholder="Search books" onChange={(e) => setSearchTerm(e.target.value)} />
      {filteredBooks.map(book => <BookItem key={book.id} book={book} />)}
    </div>
  );
}

export default EffectMemo;
